Tombloo.Service.actions.register(	{
	name : 'Download 8tracks',
	type : 'context',
	icon : "data:image/x-icon;base64,",
	check : function(ctx){
		return ctx.host == '8tracks.com';
	},
	execute : function(ctx){
		var self = this;
		var win = ctx.window;
		var page = win.location.href;
		var match = (/8tracks.com\/(.+)\/(.+)(\?|$)/).exec(page);
		var user = match[1];
		var slug = win.mix.slug;
		var title = win.mix.name;
		
		ctx.document.body.style.cursor = 'progress';
		
		models['8tracks'].getPlaylist(win.mix.id).addCallback(function(tracks){
			ctx.document.body.style.cursor = '';
		
			// プレイリストを表示しダウンロードの開始を確認する
			var msg = 'Start?: ' + user + ' - ' + title + '\n\n' + tracks.map(function(track){
				return track.number + '\t' + track.performer + ' / ' + track.name;
			}).join('\n');
			
			if(!confirm(msg))
				return;
				
			var dir = createDir(ctx.host + win.location.pathname, getDownloadDir());
			var playlist = dir.clone();
			
			// 処理が途中を表すために先頭にアンダースコアを付加する
			playlist.append('_' + user + ' - ' + slug + '.txt');
			
			putContents(playlist, tracks.map(function(track){
				var album = ['8tracks', user, title].join(': ');
				
				return [track.number, track.performer.trim(), track.name.trim(), album, 'Web', page].join('\t')
			}).join('\n'));
			
			return deferredForEach(tracks, function(track){
				var file = dir.clone();
				var ext = track.url.split('.').pop();
				file.append(user + ' - ' + slug + ' - ' + track.number.pad(3) + '.' + ext);
				
				return download(track.url, file, true);
			}).addCallback(function(){
				playlist.moveTo(null, playlist.leafName.replace(/^_/, ''));
				
				notify(self.name, 'End: ' + user + ' - ' + title, notify.ICON_DOWNLOAD);
			});
		});
	},
}, '----');
